process.on('SIGTERM', function() {
  console.log('Got SIGTERM signal.');
  exit();
});
process.on('SIGINT', function() {
  console.log('SIGINT signal');
  exit();
});
function exit(){
  console.log("");ole.log("Delay exit in 2 secs");
  setTimeout(function(){
    process.exit();
  }, 0);
}
(function wait () {
  setTimeout(wait, 1000);
})();
console.log("Now you can exit.");
